import React, { useState } from "react";
import Swal from "sweetalert2";
import { useTranslation } from "react-i18next";

const Edit = ({ employees, selectedEmployee, setEmployees, setIsEditing }) => {
  const id = selectedEmployee.id;
  const { t } = useTranslation();
  const [username, setFirstName] = useState(null);
  const [plate, setLastName] = useState(null);
  const [house, setEmail] = useState(null);
  const [model, setSalary] = useState(null);
  const [in_time, setDate] = useState(null);
  const [out_time, setOutTime] = useState(null);

  const handleUpdate = (e) => {
    e.preventDefault();

    if (!username || !plate || !house || !model || !in_time || !out_time) {
      return Swal.fire({
        icon: "error",
        title: t("error"),
        text: t("all_fields_required"),
        showConfirmButton: true,
      });
    }

    const employee = {
      id,
      username,
      plate,
      house,
      model,
      in_time,
      out_time,
    };

    for (let i = 0; i < employees.length; i++) {
      if (employees[i].id === id) {
        employees.splice(i, 1, employee);
        break;
      }
    }

    setEmployees(employees);
    setIsEditing(false);

    Swal.fire({
      icon: "success",
      title: "Actualizado!",
      text: `${employee.firstName}, ${t("license_plate")}: ${
        employee.lastName
      }, ${t("has_been_updated")}.`,
      showConfirmButton: false,
      timer: 1500,
    });
  };

  return (
    <div className="edit-screen">
      <div classname="edit-container">
        <div className="edit-box">
          <form onSubmit={handleUpdate}>
            <h1>{t("edit_vehicle")}</h1>
            <label htmlFor="firstName">{t("name")}</label>
            <input
              id="firstName"
              type="text"
              name="firstName"
              value={username}
              onChange={(e) => setFirstName(e.target.value)}
            />
            <label htmlFor="lastName">{t("license_plate")}</label>
            <input
              id="lastName"
              type="text"
              name="lastName"
              value={plate}
              onChange={(e) => setLastName(e.target.value)}
            />
            <label htmlFor="email">{t("house")}</label>
            <input
              id="email"
              type="number"
              name="email"
              value={house}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label htmlFor="salary">{t("car_model")}</label>
            <input
              id="salary"
              type="date"
              name="salary"
              value={model}
              onChange={(e) => setSalary(e.target.value)}
            />
            <label htmlFor="date">{t("date")}</label>
            <input
              id="date"
              type="date"
              name="date"
              value={in_time}
              onChange={(e) => setDate(e.target.value)}
            />
            <label htmlFor="date">{t("Out time")}</label>
            <input
              id="outTime"
              type="date"
              name="outTime"
              value={out_time}
              onChange={(e) => setOutTime(e.target.value)}
            />
            <div style={{ marginTop: "30px" }}>
              <input type="submit" value={t("update")} />
              <input
                style={{ marginLeft: "12px" }}
                className="muted-button"
                type="submit"
                value={t("cancel")}
                onClick={() => setIsEditing(false)}
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Edit;
