import React, { useState } from 'react';
import Swal from 'sweetalert2';
import { useTranslation } from 'react-i18next';

const Add = ({ employees, setEmployees, setIsAdding }) => {
  const { t } = useTranslation();
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [salary, setSalary] = useState('');
  const [date, setDate] = useState('');

  const handleAdd = async (e) => {
    e.preventDefault();

    if (!firstName || !lastName || !email || !salary || !date) {
      return Swal.fire({
        icon: 'error',
        title: t('error'),
        text: t('all_fields_required'),
        showConfirmButton: true,
      });
    }

    const newEmployee = {
      username: firstName,
      plate: lastName,
      house: email,
      model: salary,
      in_time: date,
      out_time: date, // Asumiendo que `out_time` es igual que `date` por ahora
    };

    try {
      const response = await fetch('/api/insert', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'token':localStorage.getItem('token')
        },
        body: JSON.stringify(newEmployee),
      });

      if (response.ok) {
        const addedEmployee = await response.json();
        setEmployees([...employees, { ...newEmployee, id: employees.length + 1, checked: false }]);
        setIsAdding(false);

        Swal.fire({
          icon: 'success',
          title: t('added'),
          text: `${firstName} ${t('has_been_added')}`,
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        const error = await response.json();
        Swal.fire({
          icon: 'error',
          title: t('error'),
          text: error.message,
          showConfirmButton: true,
        });
      }
    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: t('error'),
        text: t('error_adding_user'),
        showConfirmButton: true,
      });
    }
  };

  return (
    <div className='edit-screen'>
      <div className="edit-container">
        <div className="edit-box">
          <form onSubmit={handleAdd}>
            <h1>{t('add_vehicle')}</h1>
            <label htmlFor="firstName">{t('name')}</label>
            <input
              id="firstName"
              type="text"
              name="firstName"
              value={firstName}
              onChange={e => setFirstName(e.target.value)}
            />
            <label htmlFor="lastName">{t('license_plate')}</label>
            <input
              id="lastName"
              type="text"
              name="lastName"
              value={lastName}
              onChange={e => setLastName(e.target.value)}
            />
            <label htmlFor="email">{t('house')}</label>
            <input
              id="email"
              type="number"
              name="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <label htmlFor="salary">{t('car_model')}</label>
            <input
              id="salary"
              type="number"
              name="salary"
              value={salary}
              onChange={e => setSalary(e.target.value)}
            />
            <label htmlFor="date">{t('date')}</label>
            <input
              id="date"
              type="text"
              name="date"
              value={date}
              onChange={e => setDate(e.target.value)}
            />
            <div style={{ marginTop: '30px' }}>
              <input type="submit" value={t('add')} />
              <input
                style={{ marginLeft: '12px' }}
                className="muted-button"
                type="button"
                value={t('cancel')}
                onClick={() => setIsAdding(false)}
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Add;
