import React, { useState } from "react";
import Swal from "sweetalert2";
import { useTranslation } from "react-i18next";

const Add = ({ employees, setEmployees, setIsAdding }) => {
  const { t } = useTranslation();
  const [username, setFirstName] = useState("");
  const [plate, setLastName] = useState("");
  const [house, setEmail] = useState("");
  const [model, setSalary] = useState("");
  const [in_time, setDate] = useState("");
  const [out_time, setOutTime] = useState("");

  const handleAdd = async (e) => {
    e.preventDefault();

    if (!username || !plate|| !house|| !model|| !in_time || !outTime) {
      return Swal.fire({
        icon: "error",
        title: t("error"),
        text: t("all_fields_required"),
        showConfirmButton: true,
      });
    }

    const id = employees.length + 1;
    const newEmployee = {
      username,
      plate,
      house,
      model,
      in_time,
      out_time,
    };

    const response = await fetch("http://localhost:6970/insert", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        token: localStorage.getItem("token"),
      },
      body: JSON.stringify(newEmployee),
    });

    if (response.ok) {
      const addedEmployee = await response.json();
      setEmployees([
        ...employees,
        { ...newEmployee, id: employees.length + 1, checked: false },
      ]);
      setIsAdding(false);

      Swal.fire({
        icon: "success",
        title: t("added"),
        text: `${username} ${t("has_been_added")}`,
        showConfirmButton: false,
        timer: 1500,
      });
    }
  };

  return (
    <div className="edit-screen">
      <div className="edit-container">
        <div className="edit-box">
          <form onSubmit={handleAdd}>
            <h1>{t("add_vehicle")}</h1>
            <label htmlFor="firstName">{t("name")}</label>
            <input
              id="firstName"
              type="text"
              name="firstName"
              value={username}
              onChange={(e) => setFirstName(e.target.value)}
            />
            <label htmlFor="lastName">{t("license_plate")}</label>
            <input
              id="lastName"
              type="text"
              name="lastName"
              value={plate}
              onChange={(e) => setLastName(e.target.value)}
            />
            <label htmlFor="email">{t("house")}</label>
            <input
              id="email"
              type="number"
              name="email"
              value={house}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label htmlFor="salary">{t("car_model")}</label>
            <input
              id="salary"
              type="text"
              name="salary"
              value={model}
              onChange={(e) => setSalary(e.target.value)}
            />
            <label htmlFor="date">{t("In time")}</label>
            <input
              id="date"
              type="date"
              name="date"
              value={in_time}
              onChange={(e) => setDate(e.target.value)}
            />
            <label htmlFor="outTime">{t("Out time")}</label>
            <input
              id="outTime"
              type="date"
              name="outTime"
              value={out_time}
              onChange={(e) => setOutTime(e.target.value)}
            />
            <div style={{ marginTop: "50px" }}>
              <input type="submit" value={t("add")} />
              <input
                style={{ marginLeft: "12px" }}
                className="muted-button"
                type="button"
                value={t("cancel")}
                onClick={() => setIsAdding(false)}
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Add;
